import ChartBars from "./ChartBar";
import "./Chart.css";

const Chart = (props) => {
    const dataPointValues = props.datPoints.map(dataPoint => dataPoint.value);
    const totalMax = Math.max(...dataPointValues);

  return (
    <div className="chart">
      {props.datPoints.map((dataPoint) => (
        <ChartBars
          key={dataPoint.label}
          value={dataPoint.value}
          maxValue={null}
          label={dataPoint.label}
        />
      ))}
    </div>
  );
};

export default Chart;
